import React, { useState, useEffect } from "react";
import "./styles.css";

const App = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [post1, setPost1] = useState({});
  const [post2, setPost2] = useState({});

  useEffect(() => {
    fetch("https://jsonplaceholder.org/posts")
      .then((res) => res.json())
      .then((data) => {
        setPost1(data.posts[0]); // Storing first post
        setPost2(data.posts[1]); // Storing second post
      })
      .catch(console.error);
  }, []);

  return (
    <div className="container">
      <button className="toggle-button" onClick={() => setIsVisible(!isVisible)}>
        {isVisible ? "Hide" : "Show"} Text
      </button>

      {isVisible && <p className="toggle-text">This is some text that can be toggled.</p>}

      <h2>Fetched Posts</h2>
      <div className="post-item">
        <h3>{post1.title}</h3>
        <p>{post1.body}</p>
      </div>
      <div className="post-item">
        <h3>{post2.title}</h3>
        <p>{post2.body}</p>
      </div>
    </div>
  );
};

export default App;
