import React, { useState, useEffect } from "react";
import "./styles.css"; 

const App = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [data, setData] = useState([]); 

  useEffect(() => {
    fetch("https://dummyjson.com/posts") 
      .then((response) => response.json())
      .then((json) => setData(json.posts.slice(0, 5))) 
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div className="container">
      {/* Toggle Button */}
      <button className="toggle-button" onClick={() => setIsVisible(!isVisible)}>
        {isVisible ? "Hide" : "Show"} Text
      </button>

      {/* Show Text When Visible */}
      {isVisible && <p className="toggle-text">This is .</p>}

      {/* Fetch and Display API Data */}
      <h2 className="heading">Fetched Posts</h2>
      <ul className="post-list">
        {data.map((post) => (
          <li key={post.id} className="post-item">
            <h3 className="post-title">{post.title}</h3>
            <p className="post-body">{post.body}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
